package com.nikolai.mviexample.apiimport com.nikolai.mviexample.util.LiveDataCallAdapterFactoryimport retrofit2.Retrofitimport retrofit2.converter.gson.GsonConverterFactoryobject RetrofitBuilder {	const val BASE_URL = "https://open-api.xyz/"	val retrofitBuider: Retrofit.Builder by lazy {		Retrofit.Builder()				.baseUrl(BASE_URL)				.addConverterFactory(GsonConverterFactory.create())				.addCallAdapterFactory(LiveDataCallAdapterFactory())	}	val apiService: ApiService by lazy {		retrofitBuider				.build()				.create(ApiService::class.java)	}}