package com.nikolai.mviexample.base.ViewModelimport androidx.lifecycle.LiveDataimport androidx.lifecycle.MutableLiveDataimport androidx.lifecycle.ViewModelclass BaseViewModel<E, S> : ViewModel() {	 val _stateEvent: MutableLiveData<E> = MutableLiveData()	val _viewState: MutableLiveData<S> = MutableLiveData()	val viewState: LiveData<S>		get() {			return _viewState		}}