package com.nikolai.mviexample.utildata class DataState<T>(		var message: String? = null,		var errorMessage: String? = null,		var loading: Boolean = false,		var data: T? = null) {	companion object {		fun <T> error(errorMessage: String): DataState<T> {			return DataState(errorMessage = null,					data = null,					loading = false)		}		fun <T> loading(isLoading: Boolean): DataState<T> {			return DataState(errorMessage = null,					loading = isLoading,					data = null)		}		fun <T> data(errorMessage: String? = null,					 message: String? = null,					 data: T? = null): DataState<T> {			return DataState(errorMessage = errorMessage,					data = data,					message = message,					loading = false)		}	}	override fun toString(): String {		return "DataState(message=$errorMessage, loading=$loading, data=$data message=$message)"	}}